xpath.script:

document.addEventListener('DOMContentLoaded', function() {
    const invoiceCheckbox = document.getElementById('invoice_type');
    const invoiceTypeHidden = document.getElementById('invoice_type_hidden');
    const dniField = document.getElementById('dni_field');
    const invoiceFields = document.getElementById('invoice_fields');
    const dniInput = document.getElementById('dni');
    const rucInput = document.getElementById('ruc');
    const razonSocialInput = document.getElementById('razon_social');

    function toggleFields() {
        if (invoiceCheckbox.checked) {
            // Modo Factura: Ocultar DNI, mostrar campos de empresa
            dniField.style.display = 'none';
            invoiceFields.style.display = 'block';

            // Hacer requeridos los campos de factura
            rucInput.setAttribute('required', 'true');
            razonSocialInput.setAttribute('required', 'true');
            dniInput.removeAttribute('required');

            // Limpiar DNI
            dniInput.value = '';

            // Actualizar campo oculto
            invoiceTypeHidden.value = 'factura';

            console.log('Modo Factura: RUC y Razón Social requeridos, DNI opcional');
        } else {
            // Modo Boleta: Mostrar DNI, ocultar campos de empresa
            dniField.style.display = 'block';
            invoiceFields.style.display = 'none';

            // Hacer requerido el DNI
            dniInput.setAttribute('required', 'true');
            rucInput.removeAttribute('required');
            razonSocialInput.removeAttribute('required');

            // Limpiar campos de empresa
            rucInput.value = '';
            razonSocialInput.value = '';

            // Actualizar campo oculto
            invoiceTypeHidden.value = 'boleta';

            console.log('Modo Boleta: DNI requerido, campos de factura ocultos');
        }
    }

    // Validación de DNI (8 dígitos)
    dniInput.addEventListener('input', function() {
        this.value = this.value.replace(/[^0-9]/g, '').substring(0, 8);
    });

    // Validación de RUC (11 dígitos)
    rucInput.addEventListener('input', function() {
        this.value = this.value.replace(/[^0-9]/g, '').substring(0, 11);
    });

    // Ejecutar al cargar la página
    toggleFields();

    // Ejecutar cuando cambie el checkbox
    invoiceCheckbox.addEventListener('change', toggleFields);

    // Validación del formulario antes del envío
    document.querySelector('.checkout_autoformat').addEventListener('submit', function(e) {
        // Asegurar que el campo invoice_type_hidden tenga el valor correcto
        if (invoiceCheckbox.checked) {
            invoiceTypeHidden.value = 'factura';
        } else {
            invoiceTypeHidden.value = 'boleta';
        }

        if (invoiceCheckbox.checked) {
            if (!rucInput.value || rucInput.value.length !== 11) {
                e.preventDefault();
                alert('El RUC debe tener exactamente 11 dígitos');
                rucInput.focus();
                return false;
            }
            if (!razonSocialInput.value.trim()) {
                e.preventDefault();
                alert('La razón social es requerida para facturación');
                razonSocialInput.focus();
                return false;
            }
        } else {
            if (!dniInput.value || dniInput.value.length !== 8) {
                e.preventDefault();
                alert('El DNI debe tener exactamente 8 dígitos');
                dniInput.focus();
                return false;
            }
        }

        console.log('Formulario enviado con invoice_type:', invoiceTypeHidden.value);
    });

    // Declarar shippingAddressFields solo una vez
    const shippingAddressFields = document.getElementById('shipping_address_fields');
    const shippingOptionDelivery = document.getElementById('shipping_option_delivery');
    const shippingOptionPickup = document.getElementById('shipping_option_pickup');
    const requiredFields = ['street', 'city', 'country_id'];

    function setShippingFieldsRequired(required) {
        requiredFields.forEach(function(fieldId) {
            const field = document.getElementById(fieldId);
            if (field) {
                if (required) {
                    field.setAttribute('required', 'true');
                } else {
                    field.removeAttribute('required');
                }
            }
        });
    }

    function toggleShippingOption() {
        const street = document.getElementById('street');
        const city = document.getElementById('city');
        const country = document.getElementById('country_id');

        console.log('=== CAMBIANDO OPCIÓN DE ENVÍO ===');

        if (shippingOptionPickup && shippingOptionPickup.checked) {
            console.log('Cambiando a modo recogo en tienda');

            // Ocultar campos de dirección
            if (shippingAddressFields) {
                shippingAddressFields.style.display = 'none';
            }

            // Remover validación required
            setShippingFieldsRequired(false);

            // Establecer valores por defecto
            if (street) { 
                street.value = 'Sin dirección'; 
                street.readOnly = true; 
                street.style.display = 'none';
                street.removeAttribute('required');
                console.log('Campo street: valor por defecto, readonly, required removido');
            }

            if (city) { 
                city.value = 'Sin dirección'; 
                city.readOnly = true; 
                city.style.display = 'none';
                city.removeAttribute('required');
                console.log('Campo city: valor por defecto, readonly, required removido');
            }

            if (country) {
                // Buscar Perú en las opciones
                for (let i = 0; i < country.options.length; i++) {
                    if (country.options[i].text.toLowerCase().includes('peru')) {
                        country.selectedIndex = i;
                        break;
                    }
                }
                country.readOnly = true;
                country.style.display = 'none';
                country.removeAttribute('required');
                console.log('Campo country: Perú seleccionado, readonly, required removido');
            }

            console.log('✅ Modo recogo configurado correctamente');

        } else if (shippingOptionDelivery && shippingOptionDelivery.checked) {
            console.log('Cambiando a modo envío a domicilio');

            // Mostrar campos de dirección
            if (shippingAddressFields) {
                shippingAddressFields.style.display = 'block';
            }

            // Agregar validación required
            setShippingFieldsRequired(true);

            // Habilitar campos para edición
            if (street) { 
                street.readOnly = false; 
                street.value = ''; 
                street.style.display = '';
                street.setAttribute('required', 'true');
                console.log('Campo street: habilitado, vacío, required agregado');
            }

            if (city) { 
                city.readOnly = false; 
                city.value = ''; 
                city.style.display = '';
                city.setAttribute('required', 'true');
                console.log('Campo city: habilitado, vacío, required agregado');
            }

            if (country) { 
                country.readOnly = false; 
                country.selectedIndex = 0; 
                country.style.display = '';
                country.setAttribute('required', 'true');
                console.log('Campo country: habilitado, reset, required agregado');
            }

            console.log('✅ Modo envío a domicilio configurado correctamente');
        }
    }

    if (shippingOptionDelivery && shippingOptionPickup && shippingAddressFields) {
        console.log('=== INICIALIZANDO OPCIONES DE ENVÍO ===');
        console.log('shippingOptionDelivery:', shippingOptionDelivery);
        console.log('shippingOptionPickup:', shippingOptionPickup);
        console.log('shippingAddressFields:', shippingAddressFields);

        shippingOptionDelivery.addEventListener('change', function() {
            console.log('Evento change en shippingOptionDelivery');
            toggleShippingOption();
        });

        shippingOptionPickup.addEventListener('change', function() {
            console.log('Evento change en shippingOptionPickup');
            toggleShippingOption();
        });

        // Inicializar estado
        console.log('Inicializando estado de envío...');
        toggleShippingOption();
        console.log('✅ Estado de envío inicializado');
    } else {
        console.warn('⚠️ No se pudieron inicializar las opciones de envío');
        if (!shippingOptionDelivery) console.warn('shippingOptionDelivery no encontrado');
        if (!shippingOptionPickup) console.warn('shippingOptionPickup no encontrado');
        if (!shippingAddressFields) console.warn('shippingAddressFields no encontrado');
    }

    // Checkbox y campos de dirección (términos de envío)
    const shippingTermsCheckbox = document.getElementById('shipping_terms_checkbox');
    function toggleShippingFields() {
        if (shippingTermsCheckbox && shippingTermsCheckbox.checked) {
            shippingAddressFields.style.display = 'block';
        } else if (shippingOptionDelivery && shippingOptionDelivery.checked) {
            shippingAddressFields.style.display = 'none';
        }
    }
    if (shippingTermsCheckbox && shippingAddressFields) {
        toggleShippingFields();
        shippingTermsCheckbox.addEventListener('change', toggleShippingFields);
    }

    // Validación robusta antes de enviar el formulario
    const checkoutForm = document.querySelector('.checkout_autoformat');
    if (checkoutForm) {
        checkoutForm.addEventListener('submit', function(e) {
            console.log('=== VALIDACIÓN DEL FORMULARIO ===');

            // Obtener el estado actual de las opciones de envío
            const isPickup = shippingOptionPickup && shippingOptionPickup.checked;
            const isDelivery = shippingOptionDelivery && shippingOptionDelivery.checked;

            console.log('Opción de envío - Pickup:', isPickup, 'Delivery:', isDelivery);

            if (isPickup) {
                // Modo recogo: remover required de campos de dirección
                console.log('Modo recogo: removiendo validación de campos de dirección');
                requiredFields.forEach(function(fieldId) {
                    const field = document.getElementById(fieldId);
                    if (field) {
                        field.removeAttribute('required');
                        console.log(`Campo ${fieldId}: required removido`);
                    }
                });

                // Asegurar que los campos tengan valores por defecto
                const street = document.getElementById('street');
                const city = document.getElementById('city');
                const country = document.getElementById('country_id');

                if (street) street.value = 'Sin dirección';
                if (city) city.value = 'Sin dirección';
                if (country) {
                    // Buscar Perú en las opciones
                    for (let i = 0; i < country.options.length; i++) {
                        if (country.options[i].text.toLowerCase().includes('peru')) {
                            country.selectedIndex = i;
                            break;
                        }
                    }
                }

                console.log('Valores por defecto establecidos para modo recogo');

            } else if (isDelivery) {
                // Modo envío: validar que los campos de dirección estén llenos
                console.log('Modo envío: validando campos de dirección');

                let hasErrors = false;
                const errorMessages = [];

                requiredFields.forEach(function(fieldId) {
                    const field = document.getElementById(fieldId);
                    if (field) {
                        const value = field.value.trim();
                        if (!value || value === 'Sin dirección') {
                            hasErrors = true;
                            errorMessages.push(`El campo ${fieldId} es requerido para envío a domicilio`);
                            console.log(`Error: campo ${fieldId} vacío o inválido`);
                        }
                    }
                });

                if (hasErrors) {
                    e.preventDefault();
                    const errorMsg = 'Errores de validación:\n' + errorMessages.join('\n');
                    alert(errorMsg);
                    console.error('Formulario bloqueado por errores de validación');
                    return false;
                }

                console.log('Validación de envío a domicilio exitosa');
            }

            // Validación adicional del DNI/RUC según el tipo de comprobante
            const isInvoice = invoiceCheckbox && invoiceCheckbox.checked;

            if (isInvoice) {
                // Validar RUC para factura
                if (!rucInput.value || rucInput.value.length !== 11) {
                    e.preventDefault();
                    alert('El RUC debe tener exactamente 11 dígitos para facturación');
                    rucInput.focus();
                    return false;
                }
                if (!razonSocialInput.value.trim()) {
                    e.preventDefault();
                    alert('La razón social es requerida para facturación');
                    razonSocialInput.focus();
                    return false;
                }
                console.log('Validación de factura exitosa');
            } else {
                // Validar DNI para boleta
                if (!dniInput.value || dniInput.value.length !== 8) {
                    e.preventDefault();
                    alert('El DNI debe tener exactamente 8 dígitos');
                    dniInput.focus();
                    return false;
                }
                console.log('Validación de boleta exitosa');
            }

            console.log('✅ Formulario validado exitosamente, procediendo con el envío');
        });
    }
});
